import './Cart.css';
import Modal from '../UI/Modal'

const Cart = (props) => {

  const [isCartOpen, setIsCartOpen] = useState(false);

  const openCartHandler = () => {
    setIsCartOpen(true);
  };

  const closeCartHandler = () => {
    setIsCartOpen(false);
  };

  const cartItems = (
    <ul className="cart-items">
      {[{ id: 'c1', name: 'Biryani', amount: 2, price: 560 }].map((item) => (
        <li key={item.id}>
          {item.name}
        </li>
      ))}
    </ul>
  );

  // return (
  //   <Modal>
  //     {cartItems}
  //     <div className="totalAmount">
  //       <span>Total Amount</span>
  //       <span>10000</span>
  //     </div>
  //     <div className="button">
  //       <button className="btnClose">Close</button>
  //       <button className="btnOrder">Order</button>
  //     </div>
  //   </Modal>
  // );

  return (
    <div>
      {isCartOpen && (
        <div className="cart">
          {cartItems}
          <div className="totalAmount">
            <span>Total Amount</span>
            <span>10000</span>
          </div>
          <div className="button">
            <button className="btnClose" onClick={closeCartHandler}>
              Close
            </button>
            <button className="btnOrder">Order</button>
          </div>
        </div>
      )}
      <div className="cart-icon" onClick={openCartHandler}>
        Cart
      </div>
    </div>
  );

};

export default Cart;
